import React, {useContext} from "react";
import CartContext from "../store/CartContext";
import CartItem from "./CartItem"
import classes from "./Cart.module.css";
import Modal from "../UI/Modal";

export const Cart = (props) => {

  const context = useContext(CartContext)
  // console.log(`${context} is here`)

  const totalAmount = context.totalAMount

  const hasItems = context.items.length > 0;


  const cartItemAddHandler = (item) => {

  }

  const cartItemRemoveHandler = (id)=>{

  }


  const cartItems = (
    <ul className={classes["cart-items"]}>
      {context.items.map(
        (item) => (
         <CartItem key={item.id} price={item.price} name={item.name} amount={item.amount} onAdd={cartItemAddHandler} onRemove={cartItemRemoveHandler}/>
        )
      )}
    </ul>
  );



  return (
    <Modal onhandleCartCloseClick={props.handleCartCloseClick} isCartOpen={props.cartOpen}>
      {cartItems}
      <div className={classes.total}>
        <span>Total Amount</span>
        <span>{totalAmount}</span>
      </div>
      <div className={classes.actions}>
        <button onClick={props.handleCartCloseClick} className={classes["button--alt"]}>Close</button>
        {hasItems && <button className={classes.button}>Order</button>}
      </div>
    </Modal>
  );
};
