import React, { useState, useEffect } from "react";
import  {ProductItem} from "./ProductItems/ProductItem";
import { Card } from "../UI/Card";
import classes from "./Products.module.css";


export const Products = () => {
  const [product, setProduct] = useState([]);

  const [isLoading,setIsLoading] = useState(false);

  useEffect(() => {
    setIsLoading(true)
    fetch("https://fakestoreapi.com/products")
      .then((res) => res.json())
      .then((res) => setProduct(res),setIsLoading(false))
      .catch((err) => console.log(err));
  }, []);

  const shoppingProduct = product.map((item) => (
    <ul key={item.id} id={item.id} className={classes.productbox}>
      <ProductItem id={item.id} products={item} />
    </ul>
  ));
  return (
    <>

      {<Card>{isLoading} ? <div>Products Loading...Please Wait!</div> : {shoppingProduct}</Card>}
    </>
  );
};
